(3980, 'MPASS-GE 2151', 443, 17, 'Violin Orchestral Repertoire', 'A performance class devoted to precise understanding of orchestral excerpts from standard repertoire found on auditions. Students study includes tempo, dynamics, articulation & precision.', 0, '', 1, 0),
(3981, 'MPASS-GE 2152', 443, 17, 'Viola Orchestral Repertoire', 'A performance class devoted to precise understanding of orchestral excerpts from standard repertoire found on auditions. Students study includes tempo, dynamics, articulation & precision.', 0, '', 1, 0),
